import Vue from 'vue';
import VueRouter from 'vue-router';
import Home from '../views/Home.vue';
import Login from '../views/login';
import Redirect from '../views/redirect';
import config from '../config/index';

const serverType = 'school-book-admin';

Vue.use(VueRouter);

// 布局框架
import Layout from '../views/layout';

/**
 * Note: sub-menu only appear when route children.length >= 1
 * Detail see: https://panjiachen.github.io/vue-element-admin-site/guide/essentials/router-and-nav.html
 *
 * hidden: true                   if set true, item will not show in the sidebar(default is false)
 * alwaysShow: true               if set true, will always show the root menu
 *                                if not set alwaysShow, when item has more than one children route,
 *                                it will becomes nested mode, otherwise not show the root menu
 * redirect: noRedirect           if set noRedirect will no redirect in the breadcrumb
 * name:'router-name'             the name is used by <keep-alive> (must set!!!)
 * meta : {
    roles: ['admin','editor']    control the page roles (you can set multiple roles)
    title: 'title'               the name show in sidebar and breadcrumb (recommend set)
    icon: 'svg-name'/'el-icon-x' the icon show in the sidebar
    noCache: true                if set true, the page will no be cached(default is false)
    affix: true                  if set true, the tag will affix in the tags-view
    breadcrumb: false            if set false, the item will hidden in breadcrumb(default is true)
    activeMenu: '/example/list'  if set path, the sidebar will highlight the path you set
  }
 */

export const routes = [
  {
    path: '/redirect',
    component: Layout,
    hidden: true,
    children: [
      {
        path: '/redirect/:path(.*)',
        component: Redirect
      }
    ]
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    meta: {
      title: '跳转登录中'
    },
    hidden: true
  },
  {
    path: '/sso/auth',
    redirect: '/home',
    hidden: true
  },
  {
    path: '/',
    name: 'Layout',
    redirect: '/home',
    component: Layout,
    children: [
      {
        path: '/home',
        name: 'Dashboard',
        component: Home,
        meta: { title: '首页', icon: 'dashboard' }
      },
      {
        path: '/statisticalChart',
        name: 'statisticalChart',
        component: () =>
          import(
            /* webpackChunkName: "courseScore" */ '../views/statisticalChart'
          ),
        meta: { title: '统计图' }
      },
      {
        path: '/menuManage',
        name: 'menuManage',
        component: () =>
          import(/* webpackChunkName: "courseScore" */ '../views/menuManage'),
        meta: { title: '目录管理' }
      },
      {
        path: '/catalogManage',
        name: 'catalogManage',
        component: () =>
          import(
            /* webpackChunkName: "courseScore" */ '../views/catalogManage'
          ),
        meta: { title: '目录管理' }
      },
      {
        path: '/editArticle',
        name: 'editArticle',
        component: () =>
          import(
            /* webpackChunkName: "courseScore" */ '../views/catalogManage/Edit.vue'
          ),
        meta: { title: '文章管理' }
      },
      // {
      //   path: '/editArticle',
      //   name: 'editArticle',
      //   component: () =>
      //     import(
      //       /* webpackChunkName: "courseScore" */ '../views/menuManage/Edit.vue'
      //     ),
      //   meta: { title: '文章管理' }
      // },
      {
        path: '/messageManage',
        name: 'messageManage',
        component: () =>
          import(
            /* webpackChunkName: "courseScore" */ '../views/messageManage'
          ),
        meta: { title: '留言管理' }
      },
      {
        path: '/messageReply',
        name: 'messageReply',
        component: () =>
          import(
            /* webpackChunkName: "courseScore" */ '../views/messageManage/MessageReply.vue'
          ),
        meta: { title: '留言回复' }
      }
    ]
  },
  {
    path: '/error',
    name: 'Layout',
    hidden: true,
    component: Layout,
    children: [
      {
        path: '/error/NotFound',
        name: 'NotFound',
        component: () =>
          import(
            /* webpackChunkName: "sims-not-found" */ '../views/demo/NotFound.vue'
          ),
        meta: { title: '404', icon: 'dashboard' }
      }
    ]
  }
];

// 异步加载路由
export const asyncRoutes = [
  // {
  //   path: '/test',
  //   component: Layout,
  //   redirect: '/test/dynamic-form',
  //   meta: {
  //     title: 'Demo',
  //     icon: 'example'
  //   },
  //   children: [
  //     {
  //       path: '/test/dynamic-form',
  //       name: 'DynamicFormPage',
  //       meta: {
  //         title: '动态表单演示'
  //       },
  //       component: () =>
  //         import(
  //           /* webpackChunkName: "sims-dynamic-form" */ '../views/test/DynamicFormPage/index'
  //         )
  //     }
  //   ]
  // },

  // {
  //   path: '/editArticle',
  //   name: 'editArticle',
  //   component: () =>
  //     import(
  //       /* webpackChunkName: "courseScore" */ '../views/menuManage/Edit.vue'
  //     ),
  //   meta: { title: '文章管理' }
  // },
  {
    path: '/question',
    name: 'Question',
    redirect: '/question/classify-list',
    meta: {
      title: '问答管理',
      icon: 'form'
    },
    component: Layout,
    children: [
      {
        path: '/question/classify-list',
        name: 'ClassifyList',
        meta: {
          title: '问题分类'
        },
        component: () =>
          import(
            /* webpackChunkName: "classify-list" */ '../views/questManage/ClassifyList.vue'
          )
      },
      {
        path: '/question/quest-list',
        name: 'QuestList',
        meta: {
          title: '问题管理'
        },
        component: () =>
          import(
            /* webpackChunkName: "quest-list" */ '../views/questManage/QuestList.vue'
          )
      },
      {
        path: '/question/answer-list',
        name: 'AnswerList',
        meta: {
          title: '回复管理'
        },
        hidden: true,
        component: () =>
          import(
            /* webpackChunkName: "answer-list" */ '../views/questManage/AnswerList.vue'
          )
      }
    ]
  },
  {
    path: '/system',
    component: Layout,
    redirect: '/system/user',
    meta: {
      title: '系统管理',
      icon: 'component'
    },
    children: [
      {
        path: '/system/user',
        name: 'BUserManage',
        meta: {
          title: '用户管理'
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-user-manage" */ '@/components/business/BUserManage'
          )
      },
      {
        path: '/system/role',
        name: 'BRoleManage',
        meta: {
          title: '角色管理'
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-role-manage" */ '@/components/business/BRoleManage'
          )
      },
      {
        path: '/system/role/allot-user',
        name: 'BAllotUser',
        hidden: true,
        meta: {
          title: '分配用户'
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-allot-user" */ '@/components/business/BAllotUser'
          )
      },
      {
        path: '/system/role/menu-auth',
        name: 'BRoleAuthMenu',
        hidden: true,
        meta: {
          title: '菜单授权',
          noCache: true
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-role-auth-menu" */ '@/components/business/BRoleAuthMenu'
          )
      },
      {
        path: '/system/menu',
        name: 'BMenuManage',
        meta: {
          title: '菜单管理'
        },
        props: {
          serverType: serverType,
          rootTitle: config.title
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-menu-manage" */ '@/components/business/BMenuManage'
          )
      },
      {
        path: '/system/dict',
        name: 'BDataDict',
        meta: {
          title: '数据字典'
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-data-dict" */ '@/components/business/BDataDict'
          )
      },
      {
        path: '/system/log',
        name: 'BLogManage',
        meta: {
          title: '系统日志'
        },
        props: {
          serverType: serverType
        },
        component: () =>
          import(
            /* webpackChunkName: "sims-b-log-manage" */ '@/components/business/BLogManage'
          )
      }
    ]
  },
  // NotFound路由配置必须在最后一个
  {
    path: '*',
    name: 'NotFound',
    hidden: true,
    redirect: '/error/NotFound'
  }
];

const createRouter = () =>
  new VueRouter({
    mode: 'history', // require service support
    base: process.env.VUE_APP_PUBLIC_PATH + 'school-book',
    scrollBehavior: () => ({ y: 0 }),
    routes: routes
  });

const router = createRouter();

// // Detail see: https://github.com/vuejs/vue-router/issues/1234#issuecomment-357941465
export function resetRouter() {
  const newRouter = createRouter();
  router.matcher = newRouter.matcher; // reset router
}

// /**
//  * 生成路由
//  * @param {*} menuList 菜单列表
//  */
export function generateRoutes(menuList) {
  if (!Array.isArray(menuList)) menuList = [];
  let menuUrlList = menuList.map(item => item.url);
  let fn = function(arr) {
    let list = arr.filter(item => {
      let res = menuUrlList.indexOf(item.path) !== -1;
      res &&
        Array.isArray(item.children) &&
        item.children.length &&
        (item.children = fn(item.children));
      return res;
    });
    return list;
  };
  let tree = fn(asyncRoutes);
  // 添加404路由配置，因为*匹配需要放最后面。
  tree.push(asyncRoutes[asyncRoutes.length - 1]);
  return tree;
}

export default router;
